mapper.update:
UPDATE reader_book
SET return_date = #{readerBook.returnDate}, borrow_date = #{readerBook.borrowDate},
    status      = #{readerBook.status}
WHERE cred_num = #{readerBook.credNum} AND barCode = #{readerBook.barCode}

mapper.select:
SELECT *
FROM reader_book
WHERE cred_num = #{credNum}
ORDER BY borrow_date DESC
LIMIT #{start}, #{itemNumEveryPage}

SELECT count(*)
FROM reader_book
WHERE cred_num = #{credNum}

SELECT *
FROM reader_book
WHERE cred_num = #{credNum} AND barCode = #{barCode}

SELECT *
FROM reader_book
WHERE return_date < #{today}
      AND status = '超期'
ORDER BY return_date DESC
LIMIT #{start}, #{itemNumEveryPage};

SELECT *
FROM reader_book
WHERE bookNO IN

    #{bookNO}

ORDER BY borrow_date DESC;

SELECT *
FROM reader_book
WHERE cred_num = #{credNum} AND barCode = #{barCode} AND status = #{status};

SELECT count(*)
FROM reader_book
WHERE bookNO = #{bookNO}

SELECT *
FROM reader_book
WHERE bookNO = #{bookNO}
ORDER BY borrow_date DESC
LIMIT #{start}, #{itemCountEveryPage}
