mapper.update:
UPDATE reader_book
SET return_date = #{readerBook.returnDate}, borrow_date = #{readerBook.borrowDate},
    status= #{readerBook.status}
WHERE cred_num = #{readerBook.credNum} AND bookNO = #{readerBook.bookNO}

mapper.select:
SELECT *
FROM reader_book
WHERE cred_num = #{credNum}
ORDER BY borrow_date DESC
LIMIT #{start}, #{itemNumEveryPage}

SELECT count(*)
FROM reader_book
WHERE cred_num = #{credNum}

SELECT *
FROM reader_book
WHERE cred_num = #{credNum} AND bookNO = #{bookNO};
